package firstday;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      // int j = 2;
		String j = "Two";
      
      switch(j){
      case "Zero":
    	  System.out.println("j equal to 0");
    	  break;
      case "One":
    	  System.out.println("j equal to 1");
    	  break;
      case "Two":
    	  System.out.println("j equal to 2");
    	  break;
      default:
    	  System.out.println("default value");
    	  break;
    	  
      }
	}

}
